class kontenFl extends StatelessWidget {
  final String gambar;
  final String text;
  final String info1;
  final String info2;
  final String info3;
  const kontenFl({super.key, 
  required this.gambar, 
  required this.text,
  required this.info1,
  required this.info2,
  required this.info3,
  });

  @override
  Widget build(BuildContext context) {
    return Container(
            width: 130,
            padding: EdgeInsets.all(10),
            decoration: BoxDecoration(
              color: ClassName.color5,
              borderRadius: BorderRadius.circular(20),
            ),
            child: Column(
              children: [
                SizedBox(
                  width: double.infinity,
                  child: Image.network(gambar, fit: BoxFit.cover,),
                ),
                Container(
                  height: 15,
                  margin: EdgeInsets.symmetric(vertical: 10),
                  width: double.infinity,
                  decoration: BoxDecoration(
                    color: ClassName.color1,
                    borderRadius: BorderRadius.only(
                      topRight: Radius.circular(25),
                    ),
                  ),
                  child: Center(
                    child: Text(
                      text, 
                      style: GoogleFonts.openSans(
                        fontSize: 8, 
                        color: ClassName.color5,
                      ),
                    ),
                  ),
                ),
                //ini untuk informasi produk
                Text(info1, style: GoogleFonts.openSans(fontSize: 8, fontWeight: FontWeight.w400),),
                Text(info2, style: GoogleFonts.openSans(fontSize: 8, fontWeight: FontWeight.w400),),
                Text(info3, style: GoogleFonts.openSans(fontSize: 12, fontWeight: FontWeight.bold),),
                Text("Produk Online", style: GoogleFonts.openSans(fontSize: 6, fontWeight: FontWeight.bold, color: Colors.green),),
              ],
